import { nanoid } from "nanoid";
import { useEffect, useState } from "react";
import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css"
import MealItem from "./MealItem/MealItem";
// const DUMMY_MEALS = [
//     {
//         id: 'm1',
//         name: 'Sushi',
//         description: 'Finest fish and veggies',
//         price: 22.99,
//     },
//     {
//         id: 'm2',
//         name: 'Schnitzel',
//         description: 'A german specialty!',
//         price: 16.5,
//     },
//     {
//         id: 'm3',
//         name: 'Barbecue Burger',
//         description: 'American, raw, meaty',
//         price: 12.99,
//     },
//     {
//         id: 'm4',
//         name: 'Green Bowl',
//         description: 'Healthy...and green...',
//         price: 18.99,
//     },
// ];

const AvailableMeals = () => {

    const [meals, setMeals] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    useEffect(() => {
        const fetchmeals = async () => {
            // setIsLoading(true)
            const res = await fetch("https://foodorderapp-f97de-default-rtdb.firebaseio.com/meals.json")
            const data = await res.json()
            console.log(data)

            const loadedMaels = []
            for (let key in data) {
                meals.push({ id: key, ...data[key] })
            }

            console.log(meals)
            setMeals(loadedMaels)
            setIsLoading(false)
        }
        fetchmeals()
    }, [])



    const mealsList = meals.map((meal) => <MealItem key={nanoid()} name={meal.name} price={meal.price} decription={meal.description} id={meal.id} />)

    // if (isLoading) {
    //     return (
    //         <section className={classes.MealsIsLoading}>
    //             <p>Loading...</p>
    //         </section>
    //     )
    // }

    return (

        <section className={classes.meals}>
            <Card>
                <ul>
                    {mealsList}
                </ul>
            </Card>
        </section>
    )
}

export default AvailableMeals
